mapper.sql:
WHERE permissionuser.id is not NULL
AND permissionuser.deleted = #{deleted}
AND permissionuser.created_at >= CONCAT(#{sdate}, ' 00:00:00')
AND CONCAT(#{fdate}, ' 23:59:59') >= permissionuser.created_at
AND permissionuser.permission_id = #{permissionId}
AND permissionuser.user_id = #{userId}
